<script>
export default {
  props: ["openModal"],
  data() {
    return {
      page: 1,
      showsetting: true,
      namecolor: [
        "Đen mờ",
        "Đen bóng",
        "Trắng bóng",
        "Xám bóng",
        "Xanh nước biển",
        "Đỏ đua bóng",
        "Vàng hướng dương tráng bóng",
        "Rolling sea Coated",
        "Kem Ngà Tráng bóng",
        "Đỏ máu bọc vinyl nhôm bóng",
        "Siêu sương mù bạc bọc vinyl nhôm bóng",
        "Xanh lục bọc vinyl nhôm bóng",
        "Đen xanh lá",
        "Nâu"
      ],
      hexcolor: [
        "#0f1110",
        "#0d0f0e",
        "#e4e4e4",
        "#9e9e9e",
        "#47a4dd",
        "#c6312d",
        "#d39027",
        "#eb1932",
        "#d7ccb6",
        "#bf191d",
        "#b3adad",
        "#366549",
        "#545465",
        "#876c5b"
      ],
      dsnamecolor: "Min",
      colorbefore: 1,
      menutextbefore: 1,
      color: null,
      carpart: 0
    };
  },
  methods: {
    changeColor(number) {
      this.dsnamecolor = this.namecolor[this.colorbefore];
      document.getElementsByClassName(
        this.$style["colorselect" + this.colorbefore]
      )[0].style.boxShadow = "none";
      document.getElementsByClassName(
        this.$style["colorselect" + this.colorbefore]
      )[0].style.border = "none";

      this.dsnamecolor = this.namecolor[number];
      document.getElementsByClassName(
        this.$style["colorselect" + number]
      )[0].style.boxShadow = "0 0 5px #4cc7f4, inset 0 0 5px #4cc7f4";
      document.getElementsByClassName(
        this.$style["colorselect" + number]
      )[0].style.border = "1px solid #a9e3ff";
      this.colorbefore = number;
      this.color = this.hexcolor[number];
    },
    changePage(number) {
      if (number == 0) {
        if (this.page > 1) {
          this.page = this.page - 1;
        }
      }
      if (number == 1) {
        if (this.page < 4) {
          this.page = this.page + 1;
        }
      }
    },
    changeCarPart(number) {
      this.carpart = number;
      const menutext = Number(number) + 1;
      document.getElementsByClassName(
        this.$style["menutext" + this.menutextbefore]
      )[0].style.color = "#ffffff";
      document.getElementsByClassName(
        this.$style["menutext" + menutext]
      )[0].style.color = "#48ceff";
      this.menutextbefore = menutext;
    },
    openPriceCar(){
      this.$emit("openPriceCar");
    }
  },
  watch: {
    openModal: function(newVal, oldVal) {
      // watch it
      if (newVal == 1) {
        this.showsetting = true;
      } else {
        this.showsetting = false;
      }
